import * as actionTypes from '../actions/actions';


const initialState = {
    currentUser: {},
    contacts: [],
    currentRoom: {},
    messages: []
};

const chatReducer = (state = initialState, action) => {
    switch (action.type){
        case actionTypes.CHAT_INIT_SUCCESS:
            return {
                ...state, currentUser: action.currentUser
            }
        default: 
            return state;
    }
}

export default chatReducer;
