import React, {Component} from 'react'; 
import classes from './SideDrawer.module.css';
class SideDrawer extends Component {
        componentDidMount(){
        }
        createNewGroup = () => {
            console.log(this.props.currentUser);
        }
    render (){
    let sideDrawer = null;
    if (this.props.show){
        sideDrawer = (
        <div className={classes.SideDrawer}>
            <div onClick={this.createNewGroup}>Create a new group</div>
            <div onClick={this.joinGroup}>Join a group</div>
            <div onClick={this.addNewContact}>Add a new contact</div>
        </div>
        );
    }
    return sideDrawer;
}
}

export default SideDrawer;
