import React, { Component} from 'react';
import classes from './Video.module.css';
import pusher from '../../pusherConfig'; 
import { connect } from 'react-redux';
class Video extends Component {

    state = {
        usersOnline: 0,
          id: null,
          users: [],
          sessionDesc: null,
          currentcaller: '',
          room: '',
          caller: ''
    }
          //localUserMedia;
    componentDidMount (){
        this.channel = pusher.subscribe("presence-videocall");
        this.GetRTCPeerConnection();
        this.GetRTCSessionDescription();
        this.GetRTCIceCandidate();
        this.prepareCaller();
        let users = [];
        this.callUser(this.props.callTo);
        this.channel.bind("pusher:subscription_succeeded", members => {
          //set the member count
          this.setState({usersOnline: members.count, id: this.channel.members.me.id, room: this.props.callTo});

          //document.getElementById("myid").innerHTML = ` My caller id is : ` + id;
          members.each(member => {
            if (member.id != this.channel.members.me.id) {
              users.push(member.id);
        this.setState({users: users})
            }
          });

          //render();
        });

        this.channel.bind("pusher:member_added", member => {
          users.push(member.id);
        this.setState({users: users})
          //render();
        });

        this.channel.bind("pusher:member_removed", member => {
          // for remove member from list:
          //var index = users.indexOf(member.id);
        users = users.filter(user => user !== member.id);
          //users.splice(index, 1);
          if (member.id == this.state.room) {
            this.endCall();
          }
          //render();
        });
         //Listening for the candidate message from a peer sent from onicecandidate handler
         this.channel.bind("client-candidate", function(msg) {
            if (msg.room == this.state.room) {
              console.log("candidate received");
              this.caller.addIceCandidate(new RTCIceCandidate(msg.candidate));
            }
          });

          //Listening for Session Description Protocol message with session details from remote peer
          this.channel.bind("client-sdp", function(msg) {
            if (msg.room == this.props.userId) {
              console.log("sdp received");
              let answer = window.confirm(
                "You have a call from: " + msg.from + "Would you like to answer?"
              );
              if (!answer) {
                return this.channel.trigger("client-reject", { room: msg.room, rejected: this.props.userId });
              }
              this.setState({room: msg.room});
              this.getCam()
                .then(stream => {
                  this.localUserMedia = stream;
                  this.toggleEndCallButton();
                 /* if (window.URL) {
                    document.getElementById("selfview").src = window.URL.createObjectURL(
                      stream
                    );
                  } else {
                    document.getElementById("selfview").src = stream;
                  }*/
             this.localVideoRef.srcObject = stream;
                  this.caller.addStream(stream);
                  let sessionDesc = new RTCSessionDescription(msg.sdp);
                  this.caller.setRemoteDescription(sessionDesc);
                  this.caller.createAnswer().then(sdp => {
                    this.caller.setLocalDescription(new RTCSessionDescription(sdp));
                    this.channel.trigger("client-answer", {
                      sdp: sdp,
                      room: this.state.room
                    });
                  });
                })
                .catch(error => {
                  console.log("an error occured", error);
                });
            }
          });

          //Listening for answer to offer sent to remote peer
          this.channel.bind("client-answer", function(answer) {
            if (answer.room == this.state.room) {
              console.log("answer received");
              this.caller.setRemoteDescription(new RTCSessionDescription(answer.sdp));
            }
          });

          this.channel.bind("client-reject", answer => {
            if (answer.room == this.state.room) {
              console.log("Call declined");
              alert("call to " + answer.rejected + "was politely declined");
              this.endCall();
            }
          });

          this.channel.bind("client-endcall", answer =>  {
            if (answer.room == this.state.room) {
              console.log("Call Ended");
              this.endCall();
            }
          });
    }
       /* function render() {
          var list = "";
          users.forEach(function(user) {
            list +=
              `<li>` +
              user +
              ` <input type="button" style="float:right;"  value="Call" onclick="callUser('` +
              user +
              `')" id="makeCall" /></li>`;
          });
          document.getElementById("users").innerHTML = list;
        }*/

        //To iron over browser implementation anomalies like prefixes

       prepareCaller = () => {
          //Initializing a peer connection
          this.caller = new window.RTCPeerConnection();
          //Listen for ICE Candidates and send them to remote peers
          this.caller.onicecandidate = evt => {
            if (!evt.candidate) return;
            console.log("onicecandidate called");
            this.onIceCandidate(this.caller, evt);
          };
          //onaddstream handler to receive remote feed and show in remoteview video element
          this.caller.onaddstream = evt => {
            console.log("onaddstream called");
            //if (window.URL) {
              //document.getElementById("remoteview").src = window.URL.createObjectURL( evt.stream );
           // } else {
              //document.getElementById("remoteview").src = evt.stream;
           // }
        this.remoteVideoRef.srcObject = evt.stream;
          };
        }
        getCam = () => {
          //Get local audio/video feed and show it in selfview video element
          return navigator.mediaDevices.getUserMedia({
            video: true,
            audio: true
          });
        }

        GetRTCIceCandidate = () => {
          window.RTCIceCandidate =
            window.RTCIceCandidate ||
            window.webkitRTCIceCandidate ||
            window.mozRTCIceCandidate ||
            window.msRTCIceCandidate;

          return window.RTCIceCandidate;
        }

    GetRTCPeerConnection = () => {
          window.RTCPeerConnection =
            window.RTCPeerConnection ||
            window.webkitRTCPeerConnection ||
            window.mozRTCPeerConnection ||
            window.msRTCPeerConnection;
          return window.RTCPeerConnection;
        }

         GetRTCSessionDescription = () => {
          window.RTCSessionDescription =
            window.RTCSessionDescription ||
            window.webkitRTCSessionDescription ||
            window.mozRTCSessionDescription ||
            window.msRTCSessionDescription;
          return window.RTCSessionDescription;
        }

        //Create and send offer to remote peer on button click
       callUser = user => {
          this.getCam()
            .then(stream => {
              //if (window.URL) {
                //document.getElementById("selfview").src = window.URL.createObjectURL( stream);
              //} else {
                //document.getElementById("selfview").src = stream;
             // }
            this.localVideoRef.srcObject = stream;
              this.toggleEndCallButton();
              this.caller.addStream(stream);
              this.localUserMedia = stream;
              this.caller.createOffer().then(desc => {
                this.caller.setLocalDescription(new RTCSessionDescription(desc));
                this.channel.trigger("client-sdp", {
                  sdp: desc,
                  room: user,
                  from: this.props.userId
                });
                //room = user;
            this.setState({room: user})
              });
            })
            .catch(error => {
              console.log("an error occured", error);
            });
        }

     endCall = () => {
          this.setState({room: undefined});
          this.caller.close();
          for (let track of this.localUserMedia.getTracks()) {
            track.stop();
          }
          this.prepareCaller();
          this.toggleEndCallButton();
        }

       endCurrentCall = () => {
          this.channel.trigger("client-endcall", {
            room: this.state.room
          });

          this.endCall();
        }

        //Send the ICE Candidate to the remote peer
        onIceCandidate = (peer, evt) => {
          if (evt.candidate) {
            this.channel.trigger("client-candidate", {
              candidate: evt.candidate,
              room: this.state.room
            });
          }
        }

         toggleEndCallButton = () => {
        /*  if (document.getElementById("endCall").style.display == "block") {
            document.getElementById("endCall").style.display = "none";
          } else {
            document.getElementById("endCall").style.display = "block";
          }*/
        }


    render () {
        return (
            <div className={classes.Video}>
                <video autoPlay muted className={classes.LocalVideo} ref={(lVid)=> this.localVideoRef = lVid}  ></video>
                <video autoPlay className={classes.remoteVideo} ref={(rVid)=> this.remoteVideoRef = rVid}  ></video>
            </div>
        )
    }
}
const mapStateToProps = state => {
    return {
        userId: state.userId
    }
}
export default connect(mapStateToProps)(Video);
