const userId = localStorage.getItem('userId');

const config = {}

caller.onicecandidate = evt => {
    if (!evt.candidate) return;
    // alert('candidate')
    onIceCandidate(caller, evt);
  };
  caller.ontrack = evt => {
    store.dispatch(onTrack(evt));
};

