import React, {Component} from 'react'; 
import classes from './SideDrawer.module.css';
class SideDrawer extends Component {
    render (){
    let sideDrawer = null;
    if (this.props.show){
        sideDrawer = (
        <div className={classes.SideDrawer}>
            <div onClick={props.CreateNewGroup}>Create a new group</div>
            <div onClick={props.JoinGroup}>Join a group</div>
            <div onClick={props.AddNewContact}>Add a new contact</div>
        </div>
        );
    }
    return sideDrawer;
}
}

export default SideDrawer;
