import React, {Component} from 'react'; 
import classes from './SideDrawer.module.css';
import axios from '../../../Axios';
import { connect } from 'react-redux';
import JoinableRooms from './JoinableRooms/JoinableRooms';  



class SideDrawer extends Component {
        state = {
            showCreateForm: false,
            showNewChatForm: false,
            groupName: '',
            participants: [],
            potentialParticipants: [],
            privateChatParticipant: '',
            joinableRooms: [],
            ShowJoinableRoomsList: false
        }
        createNewGroupHandler = () => {
            let potentialParticipants = this.props.user.rooms.filter(u => u.isPrivate);
            this.setState(prevState => {
                return {
                showCreateForm: !prevState.showCreateForm, 
                potentialParticipants, 
                ShowJoinableRoomsList: false,
                showNewChatForm: false
             }
            });
        }
        onChange = event => {
            this.setState({[event.target.name]: event.target.value});
        }
        onCreateNewGroup = event => {
            event.preventDefault();
            console.log(event);
        }
        startNewChatHandler = () => {
            this.setState(prevState => {
                return {
                showNewChatForm: !prevState.showNewChatForm, 
                ShowJoinableRoomsList: false, 
                showCreateForm: false
            }})

        }
        onStartNewChat = event => {
            event.preventDefault();
            axios.get(`/search/${this.state.privateChatParticipant}`, {headers: {'x-auth-token': this.props.token}})
            .then(res => {
                this.props.user.createRoom({
                name: this.state.privateChatParticipant,
                private: true,
                addUserIds: [this.state.privateChatParticipant],
                customData: {displayImage: res.data.avatar}     
            })
                .then(res => console.log(res))
                .catch(err => console.log(err));
            })
            .catch(err => console.log(err));
        }
        joinGroup = () => {
            this.props.user.getJoinableRooms()
            .then(rooms => {
                console.log(rooms);
              this.setState(prevState => {
                  return {
                  joinableRooms: rooms, 
                  ShowJoinableRoomsList: !prevState.ShowJoinableRoomsList,
                  showCreateForm: false,
                  showNewChatForm: false
                }
                });

            })
            .catch(err => {
              console.log(`Error getting joinable rooms: ${err}`)
            })
        }
    render (){
    let sideDrawer = null;
    if (this.props.show){
        sideDrawer = (
        <div className={classes.SideDrawer}>
            <div onClick={this.createNewGroupHandler}>Create a new group</div>
            <form onSubmit={this.onCreateNewGroup} style={{display: this.state.showCreateForm ? 'block' : 'none'}}>
                <input onChange={this.onChange} type='text' name='groupName' placeholder='Enter group name' />
                <h3>Add participants</h3>
                {this.state.potentialParticipants.map((pp, i) => {
                    return <span key={i}>Potential user</span>
                })}
                <button>Submit</button>
            </form>
            <div onClick={this.joinGroup}>Join a group</div>
            <span style={{display: this.state.ShowJoinableRoomsList ? 'block' : 'none'}} className={classes.RoomsList}>

                    {/* {this.state.joinableRooms.map((room, i) => {
                        return <li key={i}>{room.name}</li>
                    })} */}
                    <JoinableRooms joinableRooms={this.state.joinableRooms} />

            </span>
            <div onClick={this.startNewChatHandler}>Start a new chat</div>
            <form onSubmit={this.onStartNewChat} style={{display: this.state.showNewChatForm ? 'block' : 'none'}}>
                <input onChange={this.onChange} type='text' name='privateChatParticipant' placeholder='W?' />
                <button>Submit</button>
            </form>
        </div>
        );
    }
    return sideDrawer;
}
}
const mapStateToProps = state => {
    return {
        token: state.token,
        userId: state.userId
    }
}
export default connect(mapStateToProps)(SideDrawer);
