import React, { Component} from 'react';
import classes from './Video.module.css';
import pusher from '../../pusherConfig'; 
import { connect } from 'react-redux';
import * as callActionCreators from '../../store/actions/actionIndex'

class Video extends Component {

    state = {
        usersOnline: 0,
          id: null,
          users: [],
          sessionDesc: null,
          currentcaller: '',
          room: '',
    }

    componentDidMount (){

        // this.GetRTCPeerConnection();
         //this.GetRTCSessionDescription();
         //this.GetRTCIceCandidate();
        // this.prepareCaller();
        this.callUser(this.props.callTo);
        let users = [];

        this.props.channel.bind("pusher:subscription_succeeded", members => {
          //set the member count
          console.log(members);
          this.setState({usersOnline: members.count, id: this.props.channel.members.me.id, room: this.props.callTo});


          members.each(member => {
            if (member.id !== this.props.channel.members.me.id) {
              users.push(member.id);
        this.setState({users: users})
            }
          });

        });

        this.props.channel.bind("pusher:member_added", member => {
          users.push(member.id);
          alert('member added', member);
        this.setState({users: users})

        });

        this.props.channel.bind("pusher:member_removed", member => {

        users = users.filter(user => user !== member.id);

          if (member.id === this.state.room) {
            this.endCall();
          }

        });
         //Listening for the candidate message from a peer sent from onicecandidate handler
         this.props.channel.bind("client-candidate", msg =>{

            if (msg.room === this.state.room) {
              this.props.addIceCandidate(new RTCIceCandidate(msg.candidate));
            }
          });


          //Listening for Session Description Protocol message with session details from remote peer
          this.props.channel.bind("client-sdp", msg => {
              console.log(msg);
          if (msg.room /*=== this.props.userId*/) {
              let answer = window.confirm(
                "You have a call from " + msg.from + ": Would you like to answer?"
              );
              if (!answer) {
                return this.props.channel.trigger("client-reject", { room: msg.room, rejected: this.props.userId });
              }
              this.setState({room: msg.room});
              this.getCam()
                .then(stream => {
                  this.localUserMedia = stream;
                  this.toggleEndCallButton();

             this.localVideoRef.srcObject = stream;
             stream.getTracks().forEach(track => this.props.addTrack(track, stream));

                  let sessionDesc = new RTCSessionDescription(msg.sdp);
                  this.props.setRemoteDescription(sessionDesc);
                  this.props.createAnswer();
                })
                .catch(error => {
                  console.log("an error occured", error);
                });
            }
          });

          //Listening for answer to offer sent to remote peer
          this.props.channel.bind("client-answer", answer => {
          if (answer.room /*=== this.state.room*/) {

              console.log("answer received");
              let sessionDesc = new RTCSessionDescription(answer.sdp);
              console.log(this);
              this.props.setRemoteDescription(sessionDesc)
            }
          });

          this.props.channel.bind("client-reject", answer => {
            if (answer.room === this.state.room) {
              console.log("Call declined");
              alert("call to " + answer.rejected + "was politely declined");
              this.endCall();
            }
          });

          this.props.channel.bind("client-endcall", answer =>  {
            if (answer.room === this.state.room) {
              console.log("Call Ended");
              this.endCall();
            }
          });

    }


        //To iron over browser implementation anomalies like prefixes

    //    prepareCaller = () => {
    //       //Initializing a peer connection
    //       this.props.caller = new window.RTCPeerConnection({'iceServers':[{'urls':'stun:stun.l.google.com:19302'}]});

    //       //Listen for ICE Candidates and send them to remote peers
    //       this.props.caller.onicecandidate = evt => {
    //         if (!evt.candidate) return;
    //         console.log("onicecandidate called");
    //         console.log(evt);
    //         this.onIceCandidate(this.props.caller, evt);
    //       };
    //       //onaddstream handler to receive remote feed and show in remoteview video element
    //       this.props.caller.ontrack = evt => {
    //         console.log(evt);
    //     this.remoteVideoRef.srcObject = evt.stream;
    //       };
    //     }
        getCam = () => {

          //Get local audio/video feed and show it in selfview video element
          return navigator.mediaDevices.getUserMedia({
            video: true,
            audio: true
          });
        }

        GetRTCIceCandidate = () => {
          window.RTCIceCandidate =
            window.RTCIceCandidate ||
            window.webkitRTCIceCandidate ||
            window.mozRTCIceCandidate ||
            window.msRTCIceCandidate;

          return window.RTCIceCandidate;
        }

    GetRTCPeerConnection = () => {
          window.RTCPeerConnection =
            window.RTCPeerConnection ||
            window.webkitRTCPeerConnection ||
            window.mozRTCPeerConnection ||
            window.msRTCPeerConnection;
          return window.RTCPeerConnection;
        }

         GetRTCSessionDescription = () => {
          window.RTCSessionDescription =
            window.RTCSessionDescription ||
            window.webkitRTCSessionDescription ||
            window.mozRTCSessionDescription ||
            window.msRTCSessionDescription;
          return window.RTCSessionDescription;
        }

        //Create and send offer to remote peer on button click
       callUser = user => {
          this.getCam()
            .then(stream => {

                this.localVideoRef.srcObject = stream;
                this.toggleEndCallButton();
                stream.getTracks().forEach(track => this.props.addTrack(track, stream));
                this.localUserMedia = stream;
                this.props.createOffer();
                //room = user;
            this.setState({room: user})

            })
            .catch(error => {
              console.log("an error occured", error);
            });
        }

     endCall = () => {
          this.setState({room: undefined});
          ///this.props.closeCaller.close();
          for (let track of this.localUserMedia.getTracks()) {
            track.stop();
          }
        //   this.prepareCaller();
          this.toggleEndCallButton();
        }

       endCurrentCall = () => {
          this.props.channel.trigger("client-endcall", {
            room: this.state.room
          });

          this.endCall();
        }

        //Send the ICE Candidate to the remote peer
        // onIceCandidate = (peer, evt) => {
        //   if (evt.candidate) {
        //     this.props.channel.trigger("client-candidate", {
        //       candidate: evt.candidate,
        //       room: this.state.room
        //     });
        //   }
        // }

         toggleEndCallButton = () => {
        /*  if (document.getElementById("endCall").style.display === "block") {
            document.getElementById("endCall").style.display = "none";
          } else {
            document.getElementById("endCall").style.display = "block";
          }*/
        }


    render () {
        console.log(this.state);
        return (
            <div className={classes.Video}>
                <video autoPlay muted className={classes.LocalVideo} ref={(lVid)=> this.localVideoRef = lVid}  ></video>
                <video autoPlay src={this.props.remoteStream} className={classes.remoteVideo} ref={(rVid)=> this.remoteVideoRef = rVid}  ></video>
            </div>
        )
    }
}
const mapStateToProps = state => {
    return {
        userId: state.auth.userId,
        caller: state.call.caller,
        channel: state.call.channel,
        remoteStream: state.call.remoteStream
    }
}
const mapDispatchToProps = dispatch => {
    return {
        addIceCandidate: (iceCandidate) => dispatch(callActionCreators.addIceCandidate(iceCandidate)),
        addTrack: (track, stream) => dispatch(callActionCreators.addTrack(track, stream)),
        setRemoteDescription: sessionDesc => dispatch(callActionCreators.setRemoteDescription(sessionDesc)),
        setLocalDescription:  sessionDesc => dispatch(callActionCreators.setLocalDescription(sessionDesc)),
        createAnswer: () => dispatch(callActionCreators.createAnswer()),
        createOffer: () => dispatch(callActionCreators.createOffer()),
    }
}
export default connect(mapStateToProps, mapDispatchToProps)(Video);
