import pusher from '../../../pusherConfig';
import * as actionTypes from '../actions';

const channel = pusher.subscribe('presence-videocall');
const caller = new window.RTCPeerConnection({'iceServers':[{'urls':'stun:stun.l.google.com:19302'}]});


channel.bind("pusher:subscription_succeeded", members => {

    //this.setState({id: this.props.channel.members.me.id, room: this.props.callTo});

  });

channel.bind("pusher:member_added", member => {

  });

channel.bind("pusher:member_removed", member => {

    if (member.id /*=== this.state.room*/) {
      //this.endCall();
    }

  });
channel.bind("client-candidate", msg => {

    // if (msg.room === this.state.room) {
      addIceCandidate(new RTCIceCandidate(msg.candidate));
    //}
  });
  channel.bind("client-sdp", msg => {

    if (msg.room /*=== this.props.userId*/) {
        let answer = window.confirm(
          "You have a call from " + msg.from + ": Would you like to answer?"
        );
        if (!answer) {
          return channel.trigger("client-reject", { room: msg.room, /*rejected: this.props.userId */});
        }
        //this.setState({room: msg.room});
        getMedia()
          .then(stream => {
            //this.localUserMedia = stream;
            // this.toggleEndCallButton();

       //this.localVideoRef.srcObject = stream;
       dispatch(onLocalStream(stream));
       stream.getTracks().forEach(track => addTrack(track, stream));

            let sessionDesc = new RTCSessionDescription(msg.sdp);
            setRemoteDescription(sessionDesc);
            createAnswer();
          })
          .catch(error => {
            console.log("an error occured", error);
          });
      }
    });

    channel.bind("client-answer", answer => {
        if (answer.room /*=== this.state.room*/) {

            console.log("answer received");
            let sessionDesc = new RTCSessionDescription(answer.sdp);

            setRemoteDescription(sessionDesc)
          }
        });

const onLocalStream = stream => {
    return {
        type: actionTypes.ON_LOCAL_STREAM,
        stream: stream
    }
}    
export const getCam = () => {

    //Get local audio/video feed and show it in selfview video element
    return dispatch => {
        getMedia().then(stream => {

            //this.localVideoRef.srcObject = stream;
            dispatch(onLocalStream(stream));
            stream.getTracks().forEach(track => addTrack(track, stream));
            //this.localUserMedia = stream;
            createOffer();
            //room = user;
        //this.setState({room: user})

        })
        .catch(error => {
          console.log("an error occured", error);
        });;
    }
  }

const getMedia = () => {
    return navigator.mediaDevices.getUserMedia({
        video: true,
        audio: true
      });
}
const prepareCaller = channel => {
    return {
        type: actionTypes.PREPARE_CALLER,
        channel: channel
    }
}
const onTrack = track => {
    return{
        type: actionTypes.ON_TRACK,
        remoteStream: track.stream
    }
}
export const getCallerReady = () => {
    return dispatch => {
     dispatch(prepareCaller(channel));
    //Listen for ICE Candidates and send them to remote peers
    caller.onicecandidate = evt => {
      if (!evt.candidate) return;
      console.log("onicecandidate called");
      console.log(evt);
      onIceCandidate(caller, evt);
    };
    //onaddstream handler to receive remote feed and show in remoteview video element
    caller.ontrack = evt => {
      console.log(evt);
  //this.remoteVideoRef.srcObject = evt.stream;
  dispatch(onTrack(evt));
    };
    }
}
export const addIceCandidate = iceCandidate => {
    console.log(iceCandidate);
    return dispatch => {
        console.log(iceCandidate);
    caller.addIceCandidate(iceCandidate);
    }
}

const onIceCandidate = (peer, evt) => {
    if (evt.candidate) {
      channel.trigger("client-candidate", {
        candidate: evt.candidate,
        // room: room
      });
    }
  }

  export const setLocalDescription = sessionDesc => {
      return dispatch => {
          console.log('setLocalDesc', sessionDesc);
        caller.setLocalDescription(sessionDesc);
      }
}

export const setRemoteDescription = sessionDesc => {
    console.log('setRemoteDesc', sessionDesc )
    return dispatch => {
        caller.setRemoteDescription(sessionDesc);
    }
}

export const addTrack = (track, stream) => {
    return dispatch => {
        caller.addTrack(track, stream);
    }
}

export const createAnswer = () => {
    return dispatch => {
        caller.createAnswer().then(sdp => {
        const sessionDesc = new RTCSessionDescription(sdp);
          setLocalDescription(sessionDesc);
          channel.trigger("client-answer", {
            sdp: sdp,
            room: 'opp'
          });
        });;
    }
}

export const createOffer = () => {
    return dispatch => {
        caller.createOffer().then(desc => {
            let sessionDesc = new RTCSessionDescription(desc);
            setLocalDescription(sessionDesc);
            channel.trigger("client-sdp", {
              sdp: desc,
              room: 'opp',
              from: 'gotzil'
            });
        })
}
}
