import React, { Component} from 'react';
import classes from './Video.module.css';
import pusher from '../../pusherConfig'; 
import { connect } from 'react-redux';
import * as callActionCreators from '../../store/actions/actionIndex'

class Video extends Component {

    state = {
          id: null,
          room: ''
    }

    componentDidMount (){

        this.callUser(this.props.callTo);

        this.props.channel.bind("client-reject", answer => {
            if (answer.room === this.state.room) {
              console.log("Call declined");
              alert("call to " + answer.rejected + "was politely declined");
              this.endCall();
            }
          });

          this.props.channel.bind("client-endcall", answer =>  {
            if (answer.room === this.state.room) {
              console.log("Call Ended");
              this.endCall();
            }
          });

    }


        //Create and send offer to remote peer on button click
    callUser = user => {
          this.props.getCam(user);
        }

     endCall = () => {
          this.setState({room: undefined});
          ///this.props.closeCaller.close();
          for (let track of this.localUserMedia.getTracks()) {
            track.stop();
          }

          this.toggleEndCallButton();
        }

       endCurrentCall = () => {
          this.props.channel.trigger("client-endcall", {
            room: this.state.room
          });

          this.endCall();
        }


         toggleEndCallButton = () => {
        /*  if (document.getElementById("endCall").style.display === "block") {
            document.getElementById("endCall").style.display = "none";
          } else {
            document.getElementById("endCall").style.display = "block";
          }*/
        }
    componentDidUpdate () {
        this.localVideoRef.srcObject = this.props.localStream;
        this.remoteVideoRef.srcObject = this.props.remoteStream;
    }

    render () {

        return (
            <div className={classes.Video}>
                <video autoPlay muted  className={classes.LocalVideo} ref={(lVid)=> this.localVideoRef = lVid}  ></video>
                <video autoPlay  className={classes.remoteVideo} ref={(rVid)=> this.remoteVideoRef = rVid}  ></video>
            </div>
        )
    }
}
const mapStateToProps = state => {
    return {
        userId: state.auth.userId,
        caller: state.call.caller,
        channel: state.call.channel,
        remoteStream: state.call.remoteStream,
        localStream: state.call.localStream
    }
}
const mapDispatchToProps = dispatch => {
    return {
        addIceCandidate: (iceCandidate) => dispatch(callActionCreators.addIceCandidate(iceCandidate)),
        addTrack: (track, stream) => dispatch(callActionCreators.addTrack(track, stream)),
        setRemoteDescription: sessionDesc => dispatch(callActionCreators.setRemoteDescription(sessionDesc)),
        setLocalDescription:  sessionDesc => dispatch(callActionCreators.setLocalDescription(sessionDesc)),
        createAnswer: () => dispatch(callActionCreators.createAnswer()),
        createOffer: () => dispatch(callActionCreators.createOffer()),
        getCam: () => dispatch(callActionCreators.getCam())
    }
}
export default connect(mapStateToProps, mapDispatchToProps)(Video);
