import React, { Component } from 'react';
import { connect } from 'react-redux';
import { ChatManager, TokenProvider } from '@pusher/chatkit-client';
import Contact from './Contact';
import Chat from './Chat';
import classes from './ChatScreen.module.css';
import SideDrawer from '../../components/UI/SideDrawer/SideDrawer';
import ChatScreenBar from '../../components/ChatScreenBar/ChatScreenBar';
import Modal from '../Modal/Modal';
import Spinner from '../../components/UI/Spinner/Spinner';
import * as callActionCreators from '../../store/actions/actionIndex';



class ChatScreen extends Component {

    state = {
        contacts: [],
        room: null,
        text: '',
        messages: [],
        showSideDrawer: false
    }

    componentDidMount () {
        const chatManager = new ChatManager({
            instanceLocator: 'v1:us1:64b7dbdb-3e59-4fad-9823-83add90cba65',
            userId: this.props.userId,
            tokenProvider: new TokenProvider({
                url: 'https://us1.pusherplatform.io/services/chatkit_token_provider/v1/64b7dbdb-3e59-4fad-9823-83add90cba65/token'
            }),
        })
      chatManager
        .connect({
            onAddedToRoom: room => {

                this.getRooms();
                this.setState({showSideDrawer: false, room: room});
                this.getMessages(room);
            }
        })
        .then(user => {
            this.currentUser = user;
            this.getRooms();

        })
        .catch(err => console.log(err));

        this.scrollToBottom();

    }

    scrollToBottom = () => {
       if (this.scrollRef && this.state.messages.length > 0){
        let lastElement = this.scrollRef.children[this.scrollRef.children.length-1];
        lastElement.scrollIntoView("smooth");
    }
}
    getRooms = () => {
        this.setState({contacts: this.currentUser.rooms});
        //Subscribe to all rooms
        this.state.contacts.map(con => {
           return  this.currentUser.subscribeToRoom({
                roomId: con.id,
                hooks: {
                  onMessage: message => {
                    //this.setState({messages: [...this.state.messages, message]});
                    this.getMessages(message.room);
                    this.scrollToBottom();
                  }
                },
                messageLimit: 100
              })
        })
    }
    getMessages = room => { 
        if (room.isPrivate) {
            this.props.privateChatInit();
        }
        this.setState({room: room});
        this.currentUser.fetchMessages({
            roomId: room['id'],
            //initialId: 42,
            direction: 'older',
            limit: 100,
          })
            .then(messages => {
              this.setState({messages});
              this.scrollToBottom();
            })
            .catch(err => {
              console.log(`Error fetching messages: ${err}`)
            })
    }

    onChange = event => {
        this.setState({text: event.target.value});
    }
    onSubmit = event => {
        event.preventDefault();
        this.currentUser.sendMessage({
            text: this.state.text,
            roomId: this.state.room.id
        })
        this.setState({text: ''});
    }

   toggleSideDrawer = () => {
        this.setState(prevState => {
            return {showSideDrawer: !prevState.showSideDrawer}});
    }

    render () {

        let chat = <div className={classes.Chat}></div>;
        if (this.state.room){
            chat = (
            <div className={classes.Chat}>
                <ChatScreenBar room={this.state.room}/>
                <div className={classes.Msgs} ref={(div) => {this.scrollRef = div}}>
                {
                    this.state.messages.map(msg => {
                        return <Chat sender={msg.senderId} byCurrentUser={this.props.userId === msg.senderId} text={msg.text} key={msg.id} />
                    })
                }
                </div>

            <form onSubmit={this.onSubmit}>
                <input onChange={this.onChange} value={this.state.text} name='text' type="text" placeholder="Enter message" />
            </form>
            </div>
            );
        } else {
            chat = (
                    <Modal show={true}>
                        <Spinner />
                    </Modal>
            );
        }
        return (
            <div className={classes.ChatScreen}>
                <div className={classes.ContactsPane} >
                <SideDrawer show={this.state.showSideDrawer} 
                user={this.currentUser}
                />
                <div className={classes.MenuBar}>
                    <i onClick = {this.toggleSideDrawer} className="fa fa-bars"></i>
                </div>
                <h1 style={{textAlign: 'center'}}>Contacts</h1>
                {this.state.room && this.state.contacts.map(con => {
                    return <Contact key={con.id} name={con.name} Active={con.id === this.state.room.id} clicked={() => this.getMessages(con)}/>
                })}
                </div>
                {chat}
            </div>
        )
    }
}

const mapStateToProps = state => {
    return {
        userId: state.auth.userId
    }
}
const mapDispatchToProps = dispatch => {
    return {
        privateChatInit: () => dispatch(callActionCreators.getCallerReady())
    }
}
export default connect(mapStateToProps, mapDispatchToProps)(ChatScreen);
