import React, {Component} from 'react'; 
import classes from './SideDrawer.module.css';
import axios from '../../../Axios';
import { connect } from 'react-redux';


class SideDrawer extends Component {
        state = {
            showCreateForm: false,
            groupName: '',
            participants: [],
            potentialParticipants: []
        }
        createNewGroupHandler = () => {
            let potentialParticipants = this.props.user.rooms.filter(u => u.isPrivate);
            this.setState({showCreateForm: true, potentialParticipants });
        }
        onChange = event => {
            this.setState({[event.target.name]: event.target.value});
        }
        onCreateNewGroup = event => {
            event.preventDefault();
            console.log(event);
        }
        startNewChat = () => {
            axios.get('/profile/opp', {headers: {'x-auth-token': this.props.token}})
                .then(res => console.log(res));
        }
    render (){
    let sideDrawer = null;
    if (this.props.show){
        sideDrawer = (
        <div className={classes.SideDrawer}>
            <div onClick={this.createNewGroupHandler}>Create a new group</div>
            <form onSubmit={this.onCreateNewGroup} style={{display: this.state.showCreateForm ? 'block' : 'none'}}>
                <input onChange={this.onChange} type='text' name='groupName' placeholder='Enter group name' />
                <h3>Add participants</h3>
                {this.state.potentialParticipants.map((pp, i) => {
                    return <span key={i}>Potential user</span>
                })}
                <button>Submit</button>
            </form>
            <div onClick={this.joinGroup}>Join a group</div>
            <div onClick={this.startNewChat}>Start a new chat</div>
        </div>
        );
    }
    return sideDrawer;
}
}
const mapStateToProps = state => {
    return {
        token: state.token
    }
}
export default connect(mapStateToProps)(SideDrawer);
