import * as actionTypes from '../actions';
import axios from '../../../Axios';

const logIn = (token, userId) => {
    return {
        type: actionTypes.LOG_IN,
        token: token, 
        userId: userId
    }
}
export const onLogIn = (data) => {
    return dispatch => {
        axios.post("/login", data)
         .then(res => {
           const { userId } = res.data;
           console.log(userId);
           localStorage.setItem("token", res.data.token);
           localStorage.setItem("userId", userId);
           dispatch(logIn(res.data.token, userId));
         })
         .catch(err => {
             dispatch(logInFailed(err))
         })
    }
}
const logInFailed = err => {
    return {
        type: actionTypes.LOG_IN_FAILED,
        error: err 
    }
}
const logout = () => {
    return {
        type: actionTypes.LOG_OUT
    }
}

export const onLogout = () => {
    return dispatch => {
        //delete token saved in local storage
        localStorage.removeItem("token");
        localStorage.removeItem("userId");
        dispatch(logout());
    }
}

const auth = (token, userId) => {
    return {
        type: actionTypes.ON_AUTH,
        token: token,
        userId: userId
    }
}
export const onAuth = () => {
    return dispatch => {
        let token = localStorage.getItem('token');
        let userId= localStorage.getItem('userId');
        if(token){
            dispatch(auth(token, userId));
        }
    }
}
const registered = () => {
    return {
        type: actionTypes.ON_REGISTER,

    }
}
const registrationFailed = err => {
    return {
        type: actionTypes.ON_REGISTRATION_FAILED,
        error: err
    }
}
export const onRegister = data => {
    return dispatch => {
        axios.post("/register", data)
            .then(res => {
                dispatch(registered())
            })
            .catch(err => {
                dispatch(registrationFailed())
            });
    }
}
