        //   });

          //Listening for answer to offer sent to remote peer
        //   this.props.channel.bind("client-answer", answer => {
        //   if (answer.room === this.state.room) {

        //       console.log("answer received");
        //       let sessionDesc = new RTCSessionDescription(answer.sdp);
        //       console.log(this);
        //       this.props.setRemoteDescription(sessionDesc)
        //     }
        //   });

        this.props.channel.bind("client-reject", answer => {
            if (answer.room === this.state.room) {
              console.log("Call declined");
              alert("call to " + answer.rejected + "was politely declined");
              this.endCall();
            }
          });

          this.props.channel.bind("client-endcall", answer =>  {
            if (answer.room === this.state.room) {
              console.log("Call Ended");
              this.endCall();
            }
          });

    }


