          }*/
        }


    render () {
        console.log(this.state);
        return (
            <div className={classes.Video}>
                <video autoPlay muted className={classes.LocalVideo} ref={(lVid)=> this.localVideoRef = lVid}  ></video>
                <video autoPlay src={this.props.remoteStream} className={classes.remoteVideo} ref={(rVid)=> this.remoteVideoRef = rVid}  ></video>
            </div>
        )
    }
}
const mapStateToProps = state => {
    return {
        userId: state.auth.userId,
        caller: state.call.caller,
        channel: state.call.channel,
        remoteStream: state.call.remoteStream
    }
}
const mapDispatchToProps = dispatch => {
    return {
        addIceCandidate: (iceCandidate) => dispatch(callActionCreators.addIceCandidate(iceCandidate)),
        addTrack: (track, stream) => dispatch(callActionCreators.addTrack(track, stream)),
        setRemoteDescription: sessionDesc => dispatch(callActionCreators.setRemoteDescription(sessionDesc)),
        setLocalDescription:  sessionDesc => dispatch(callActionCreators.setLocalDescription(sessionDesc)),
        createAnswer: () => dispatch(callActionCreators.createAnswer()),
        createOffer: () => dispatch(callActionCreators.createOffer()),
        getCam: () => dispatch(callActionCreators.getCam())
    }
}
export default connect(mapStateToProps, mapDispatchToProps)(Video);
