import React, { Component } from 'react';
import classes from './Contact.module.css';
import img from '../../images/p37605.png'
import OptionsDropbar from '../../components/UI/OptionsDropbar/OptionsDropbar'

class Contact extends Component {
    state = {
        showOptions: false,
        position: {}
    }
    showOptions = event => {
        let pos = {};
        pos.x = event.clientX + 200;
        pos.y = event.clientY;
        this.setState({position: pos, showOptions: true})
    }
    hideOptions = () => {
        this.setState({showOptions: false, position: {}})
    }

    render(){
        let attachedClasses = [classes.Contact];
        let unreadMessages = "";
        if (this.props.Active){
            attachedClasses.push(classes.Active); 
        }
        if (this.props.unopenedMessages[this.props.id]){
            unreadMessages = this.props.unopenedMessages[this.props.id].length;
        }
        return (
            <div className={attachedClasses.join(' ')} onClick={this.props.clicked}>
                <img src={img} alt=''/>
                <div>
                    <p>{this.props.name}</p>
                    <p>{this.props.lastMessage}</p>
                </div>
                <div className={classes.FloatedRight}>
                    <span>{unreadMessages}</span>
                    <i onClick={this.showOptions} className="fa fa-angle-down"></i>
                    { this.state.showOptions && 
                        <OptionsDropbar position={this.state.position} 
                                // optionClicked={(event, option) => this.optionClicked(option)}
                                show={this.state.showOptions} 
                                hideOptions={this.hideOptions}
                                options={[{name: 'Archive chat'},
                                {name: 'Mute'},
                                {name: 'Delete chat'},
                                {name: 'Pin chat'},
                                {name: 'Mark as unread'}
                                ]}/>}
                </div>
            </div>
    )
    }
}

export default Contact;
