import React, { Component } from 'react';
import classes from './Contact.module.css';
import img from '../../images/p37605.png'
import OptionsDropbar from '../../components/UI/OptionsDropbar/OptionsDropbar'

class Contact extends Component {
    state = {
        showOptions: false,
        position: {}
    }
    showOptions = event => {
        let pos = {};
        let x = event.clientX;
        let y = event.clientY;
        console.log(x, y);
    }
    render(){
        let attachedClasses = [classes.Contact];
        let unreadMessages = "";
        if (this.props.Active){
            attachedClasses.push(classes.Active); 
        }
        if (props.unopenedMessages[props.id]){
            unreadMessages = this.props.unopenedMessages[this.props.id].length;
        }
        return (
            <div className={attachedClasses.join(' ')} onClick={this.props.clicked}>
                <img src={img} alt=''/>
                <div>
                    <p>{this.props.name}</p>
                    <p>{this.props.lastMessage}</p>
                </div>
                <div className={classes.FloatedRight}>
                    <span>{unreadMessages}</span>
                    <i onClick={this.showOptions} class="fa fa-angle-down"></i>
                </div>
            </div>
    )
    }
}

export default Contact;
