import React, { Component} from 'react';
import classes from './Video.module.css';
import pusher from '../../pusherConfig'; 
class Video extends Component {

    componentDidMount () {
        this.channel = pusher.subscribe('presence-videocall');
            navigator.mediaDevices.getUserMedia({
                video: true
            })
            .then(this.gotLocalStream)
            .catch(err => console.log(err));
            this.GetRTCPeerConnection();
            this.GetRTCSessionDescription();
            this.GetRTCIceCandidate();
    //prepare the caller to use peerconnection
            this.prepareCaller();
    }

    gotLocalStream = mediaStream => {
        this.localStream = mediaStream;
        this.localVideoRef.srcObject = mediaStream;
    }

     GetRTCIceCandidate = () => {
        // this.RTCIceCandidate =
        //   this.RTCIceCandidate ||
        //   this.webkitRTCIceCandidate ||
        //   this.mozRTCIceCandidate ||
        //   this.msRTCIceCandidate;

        return window.RTCIceCandidate;
      }

       GetRTCPeerConnection = () => {
        // this.RTCPeerConnection =
        //   this.RTCPeerConnection ||
        //   this.webkitRTCPeerConnection ||
        //   this.mozRTCPeerConnection ||
        //   this.msRTCPeerConnection;
        return window.RTCPeerConnection;
      }

       GetRTCSessionDescription = () => {
        // this.RTCSessionDescription =
        //   this.RTCSessionDescription ||
        //   this.webkitRTCSessionDescription ||
        //   this.mozRTCSessionDescription ||
        //   this.msRTCSessionDescription;
        return window.RTCSessionDescription;
      }
       prepareCaller = () => {
        //Initializing a peer connection
        const caller = new window.RTCPeerConnection();

        //Listen for ICE Candidates and send them to remote peers
        caller.onicecandidate = (evt) => {
          if (!evt.candidate) return;
          console.log("onicecandidate called");
          this.onIceCandidate(caller, evt);
        };
        //onaddstream handler to receive remote feed and show in remoteview video element
        caller.onaddstream = (evt) => {
          console.log("onaddstream called");
          if (this.URL) {
            this.remoteVideoRef.srcObject = window.URL.createObjectURL(
              evt.stream
            );
          } else {
            this.remoteVideoRef.srcObject = evt.stream;
          }
        };
      }
      onIceCandidate = (peer, evt) => {
        if (evt.candidate) {
            this.channel.trigger("client-candidate", {
                "candidate": evt.candidate,
                // "room": room
            });
        }
    }
    render () {
        return (
            <div className={classes.Video}>
                <video autoPlay ref={(lVid)=> this.localVideoRef = lVid}  ></video>
                <video autoPlay ref={(rVid)=> this.remoteVideoRef = rVid}  ></video>
            </div>
        )
    }
}

export default Video;
